import "core-js/modules/es.array.push.js";
import { EventBus } from "@/main";
export default {
  name: "ActorEditProfile",
  props: {
    actorId: {
      type: String,
      required: true
    }
  },
  watch: {
    actorId: {
      handler: function () {
        this.retrieveProfileInfo();
      }
    },
    'profile.languages.value': {
      handler: function (langs) {
        this.addToLangSkills(langs);
      }
    }
  },
  created() {
    this.retrieveProfileInfo();
    EventBus.$on('actorEditionForm_actorEditProfile_save', this.save);
  },
  methods: {
    addToLangSkills(langs) {
      if (this.languagesSkill.length > 0) {} else {
        this.languagesSkill.push({
          lang: langs[0],
          percent: 0
        });
      }
    },
    save() {
      const actor = {};
      for (let profileKey in this.profile) {
        if (this.profile[profileKey].value != null) {
          actor[profileKey] = this.profile[profileKey].value;
        }
      }
      if (actor.name.length < 1 && Object.keys(actor).length < 1) {
        EventBus.$emit('alert', {
          color: 'red',
          type: 'error',
          msg: 'El perfil no puede estar vacío'
        });
        return;
      }
      EventBus.$emit('toActorEditionForm_save', actor);
    },
    retrieveProfileInfo() {
      this.axios.get(`/profile/retrieve/${this.actorId}`).then(res => {
        for (let key in this.profile) {
          this.profile[key].value = res.data[key];
        }
      }).catch(e => {
        EventBus.$emit('alert', {
          color: 'red',
          type: 'error',
          msg: e.response.data.message
        });
      });
    }
  },
  data: () => ({
    profile: {
      name: {
        label: "Nombre",
        icon: "mdi-form-textbox",
        type: "text",
        value: null
      },
      age: {
