        type: "text",
        value: null
      },
      hair: {
        label: "Cabello",
        icon: "mdi-hair-dryer-outline",
        type: "text",
        value: null
      },
      eyes: {
        label: "Ojos",
        icon: "mdi-eye-outline",
        type: "text",
        value: null
      },
      height: {
        label: "Altura",
        icon: "mdi-human-male-height",
