import "core-js/modules/es.array.push.js";
import { EventBus } from "@/main";
export default {
  name: "ActorEditSocial",
  props: {
    actorId: {
      type: String,
      required: true
    }
  },
  data: () => ({
    allNetworksForCombo: ['Twitter', 'Youtube', 'Instagram', 'Facebook', 'Web'],
    selectedNetworkNames: [],
    allNetworks: [{
      name: 'Twitter',
      icon: 'mdi-twitter',
      url: ''
    }, {
      name: 'Youtube',
      icon: 'mdi-youtube',
      url: ''
    }, {
      name: 'Instagram',
      icon: 'mdi-instagram',
      url: ''
    }, {
      name: 'Facebook',
      icon: 'mdi-facebook',
      url: ''
    }, {
      name: 'Web',
      icon: 'mdi-earth',
      url: ''
    }],
    actorNetworks: []
  }),
  watch: {
    actorId: function () {
      this.retrieveSocialInfo();
    }
  },
  created() {
    this.retrieveSocialInfo();
    EventBus.$on('actorEditionForm_actorEditSocial_save', this.save);
  },
  methods: {
    save() {
      if (this.actorNetworks.length < 1) {
        EventBus.$emit('alert', {
          color: 'red',
          type: 'error',
          msg: 'Debes indicar al menos una red'
        });
        return;
      }
      this.actorNetworks.forEach(n => delete n.icon);
      EventBus.$emit('toActorEditionForm_save', {
        media: this.actorNetworks
      });
      this.actorNetworks = [];
      this.selectedNetworkNames = [];
    },
    retrieveSocialInfo() {
      this.axios.get(`/social/retrieve/${this.actorId}`).then(res => {
        console.log(res.data);
      }).catch(e => {
        EventBus.$emit('alert', {
          color: 'red',
          type: 'error',
          msg: e.response.data.message
        });
      });
    },
    addNetwork(networksToAdd) {
      // enter the new ones from the 'networksToAdd' array
      networksToAdd.forEach(checkNetwork => {
        if (!this.actorNetworks.some(actNet => actNet.name === checkNetwork)) {
          this.actorNetworks.push(this.allNetworks.find(net => {
            if (net.name === checkNetwork) {
              return net;
            }
          }));
        }
      });
      // delete those that are not present
      this.actorNetworks = this.actorNetworks.filter(network => {
        if (networksToAdd.includes(network.name)) {
          return network;
        }
      });
    }
  }
};
