import EditOptions from "@/constants/EditOptions";
import { EventBus } from "@/main";
import ActorEditProfile from "@/components/ActorEditProfile.vue";
export default {
  name: "ActorEditionForm",
  computed: {
    EditOptions() {
      return EditOptions;
    }
  },
  components: {
    ActorEditProfile
  },
  props: {
    actorName: {
      type: String,
      required: true
    },
    actorId: {
      type: String,
      required: true
    },
    showDialog: {
      type: Boolean,
      default: false
    },
    editionType: {
      type: String,
      required: true
    }
  },
  created() {
    this.resizeDialog();
  },
  data: () => ({
    dialogWidthVW: "55vw"
  }),
  methods: {
    resizeDialog() {
      window.addEventListener('resize', () => {
        if (window.innerWidth < 700) {
          this.dialogWidthVW = 95 + 'vw';
        } else {
          this.dialogWidthVW = 55 + 'vw';
        }
      });
    },
    closeDialog() {
      EventBus.$emit('actorEditionForm_adminPanel_showEditForm');
    }
  }
};
