import "core-js/modules/es.array.push.js";
import { EventBus } from "@/main";
import { mixins } from "../../mixins";
import ActorGallery from "@/components/ActorGallery.vue";
import AdminBar from "@/components/AdminBar.vue";
import ActorEditDialog from "@/components/ActorEditDialog.vue";
export default {
  mixins: [mixins],
  name: "AdminPanel",
  components: {
    ActorEditDialog,
    AdminBar,
    ActorGallery
  },
  data: () => ({
    isAdmin: true,
    showEditDialog: false,
    selectedActor: {
      id: '',
      name: ''
    }
  }),
  created() {
    this.checkAdmin();
    EventBus.$on('adminActions_adminPanel_editActor', selectedActor => {
      this.selectedActor = selectedActor;
      this.showEditDialog = true;
    });
    EventBus.$on('actorEditDialog_adminPanel_closeDialog', () => {
      this.showEditDialog = false;
    });
  },
  methods: {
    async checkAdmin() {
      this.axios.get(`/admin/check`).then(res => {
        if (!res.data.admin) this.$router.push(`/`);
      }).catch(e => {
        this.removeSession();
        EventBus.$emit('alert', {
          color: 'red',
          type: 'error',
          msg: e.response.data.message
        });
        this.$router.push(`/`);
      });
    }
  }
};
