import EditOptions from "@/constants/EditOptions";
import { EventBus } from "@/main";
import ActorEditProfile from "@/components/ActorEditProfile.vue";
import ActorEditBackground from "@/components/ActorEditBackground.vue";
import ActorEditSocial from "@/components/ActorEditSocial.vue";
export default {
  name: "ActorEditionForm",
  computed: {
    EditOptions() {
      return EditOptions;
    }
  },
  components: {
    ActorEditSocial,
    ActorEditBackground,
    ActorEditProfile
  },
  props: {
    actorName: {
      type: String,
      required: true
    },
    actorId: {
      type: String,
      required: true
    },
    showDialog: {
      type: Boolean,
      default: false
    },
    editionType: {
      type: String,
      required: true
    }
  },
  created() {
    this.resizeDialog(window.innerWidth);
    window.addEventListener('resize', () => {
      this.resizeDialog(window.innerWidth);
    });
  },
  data: () => ({
    dialogWidthVW: "55vw"
  }),
  methods: {
    resizeDialog(iw) {
      if (iw < 1250) {
        this.dialogWidthVW = 95 + 'vw';
      } else {
        this.dialogWidthVW = 55 + 'vw';
      }
    },
    closeDialog() {
      EventBus.$emit('actorEditionForm_adminPanel_showEditForm');
    }
  }
};
