import "core-js/modules/es.array.push.js";
import { EventBus } from "@/main";
export default {
  name: "AdminPanel",
  data: () => ({}),
  created() {
    if (!this.checkAdmin()) this.$router.push(`/`);
  },
  methods: {
    async checkAdmin() {
      this.axios.get(`/admin/check`).then(res => {
        return res.data.admin;
      }).catch(e => {
        EventBus.$emit('alert', {
          color: 'red',
          type: 'error',
          msg: e.response.data.message
        });
      });
    }
  }
};
